import axios from "axios";
import React, { useEffect, useState } from "react";

const Read = () => {
  const [data, setData] = useState([]);

  function getData() {
    axios
      .get("https://6458edff4eb3f674df818906.mockapi.io/crud/api/CrudApi")
      .then((res) => {
        // console.log(res);
        setData(res.data);
      });
  }

function handelDelet (){
    axios.delete
}


  useEffect(() => {
    getData();
  }, []);

  getData();

  return (
    <div>
      <h2 className="text-center border border-warning">
        {" "}
        Read Operation Table
      </h2>
      <table class="table">
        <thead>
          <tr>
            <th scope="col">#Id</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col"></th>
            <th scope="col"></th>
          </tr>
        </thead>
        {data.map((eachData) => {
          return (
            <>
              <tbody>
                <tr>
                  <th scope="row">{eachData.id}</th>
                  <td>{eachData.name}</td>
                  <td>{eachData.email}</td>
                  <td>
                    <button className="btn btn btn-danger">Edit</button>
                  </td>
                  <td>
                    <button className="btn btn-warning" 
                    onClick={()=> handelDelet(eachData.id)
                    }
                    >Delete</button>
                  </td>
                </tr>
              </tbody>
            </>
          );
        })}
      </table>
    </div>
  );
};

export default Read;
